import { useState } from "react";

export default function AgentFlowDemo() {
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [output, setOutput] = useState(null);
  const [error, setError] = useState(null);

  const apiKey = process.env.REACT_APP_OPENAI_API_KEY;


  async function generateCopy() {
    if (!input.trim()) return;

    if (!apiKey) {
      setError("Missing OpenAI API key. Check your .env file.");
      return;
    }

    setLoading(true);
    setOutput(null);
    setError(null);

    try {
      const response = await fetch("https://api.openai.com/v1/chat/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${apiKey}`,
        },
        body: JSON.stringify({
          model: "gpt-4",
          messages: [
            {
              role: "system",
              content:
                "You are a helpful AI that creates real estate content. When given a short property blurb, respond with:\n\n1. Listing Description\n2. Instagram Caption\n3. Email Subject Line",
            },
            {
              role: "user",
              content: input,
            },
          ],
        }),
      });

      const data = await response.json();
      console.log("OpenAI response:", data);


      if (data?.choices?.[0]?.message?.content) {
        setOutput(data.choices[0].message.content);
      } else {
        setError("OpenAI did not return a valid response.");
      }
    } catch (err) {
      console.error("API error:", err);
      setError("Failed to connect to OpenAI API.");
    } finally {
      setLoading(false);
    }
  }

  return (
    <section className="w-full max-w-2xl text-center py-12">
      <h2 className="text-3xl font-bold mb-4">Try the AgentFlow Demo</h2>
      <p className="text-gray-600 mb-6">Type a property description and let AI do the rest:</p>

      <textarea
        rows={4}
        className="w-full p-4 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 resize-none mb-4"
        placeholder="e.g. Cozy 2BR apartment in central Tel Aviv with a balcony and ocean views"
        value={input}
        onChange={(e) => setInput(e.target.value)}
      />

      <button
        onClick={generateCopy}
        className="bg-blue-600 text-white px-6 py-3 rounded-full hover:bg-blue-700 transition mb-6"
        disabled={loading}
      >
        {loading ? "Generating..." : "Generate"}
      </button>

      {error && <p className="text-red-500 font-medium mb-4">{error}</p>}

      {output && (
        <div className="text-left bg-white p-6 rounded-xl shadow-md space-y-4 text-gray-800 whitespace-pre-wrap">
          {output}
        </div>
      )}
    </section>
  );
}
